import React, { Component } from "react";
import FacebookLogin from "react-facebook-login";
import ReactDOM from "react-dom";
import { doc } from "prettier";
const axios = require("axios");
// const internalApi = "https://apitechno-powers.com/technoPoers-API/";
const internalApi = "http://localhost:8080/";

const responseFacebook = (response) => {
  console.log(response, "resssssssssssss", response.accessToken);
  try {
    const apiResponse = axios.post(`${internalApi}api/users/social`, {
      method: "facebook",
      token: response.accessToken,
    });
    console.log(apiResponse, "apiiiiiiiiiiiiiiiiii");
    if (apiResponse.data.status === 200) {
      console.log(apiResponse, "response from api");
      localStorage.setItem("token", apiResponse.data.token);
      localStorage.setItem("username", apiResponse.data.username);
      localStorage.setItem("email", apiResponse.data.email);
      window.location.replace(window.location.origin);
    } else {
      // NotificationManager.warning("Email or password is incorrect.", "", 3000);
    }
  } catch (error) {
    console.log(error);
    return;
    // NotificationManager.warning("Email or password is incorrect.", "", 3000);
  }
};
export default class FacebookComponent extends Component {
  render() {
    return (
      <div>
        <FacebookLogin
          appId='747798019364772'
          autoLoad={true}
          fields='name,email,picture'
          // callback={responseFacebook}
          icon='fa-facebook'
          // icon='fa-facebook-square'
          // cssClass='fb-customisation'
          callback={responseFacebook}
        />
      </div>
    );
  }
}
