import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons

// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";

import styles from "assets/jss/material-kit-react/views/landingPageSections/workStyle.js";
import {NotificationContainer, NotificationManager} from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import { Alert } from 'reactstrap';


import Warning from "@material-ui/icons/Warning";

const useStyles = makeStyles(styles);
const axios = require('axios');
const internalApi ="http://localhost:9000/"

export default function WorkSection() {
  const classes = useStyles();
  let userName = localStorage.getItem("username") && localStorage.getItem("username") != "null" ? localStorage.getItem("username") :"Your Name"
  let userEmail = localStorage.getItem("email") && localStorage.getItem("email") != "null" ?  localStorage.getItem("email") : "Your Email";

  //   return () => {
  //     switch (type) {
  //       case 'info':
  //         NotificationManager.info('Info message');
  //         break;
  //       case 'success':
  //         NotificationManager.success('Success message', 'Title here');
  //         break;
  //       case 'warning':
  //         NotificationManager.warning('Warning message', 'Close after 3000ms', 3000);
  //         break;
  //       case 'error':
  //         NotificationManager.error('Error message', 'Click me!', 5000, () => {
  //           alert('callback');
  //         });
  //         break;
  //     }
  //   };
  // };

  function handelSubmit(){
    var name = document.getElementById("name").value ? document.getElementById("name").value : userName;
    var email = document.getElementById("email").value ? document.getElementById("email").value : userEmail;
    alert("helooooooooooo")

    if(!(name && email &&  document.getElementById("message").value)){
      console.log("test-values empty error")
      // message, title, timeOut, callback, priority
      // NotificationManager.warning('Please fill in the required fields', '', 3000,null,true);
    }else{
      console.log("call api-apisss")
      axios.post(`${internalApi}api/requestCourse`, {
        "email": email,
        "name": name,
        "message": document.getElementById("message").value
      })
      .then(function (response) {
        return NotificationManager.success('Done ,Thanks', '', 3000);
      })
      .catch(function (error) {
        return NotificationManager.error('Unexpected Error ..sorry, try again', '', 1000);
        return error;
      });
    }
  }

  return (
    <div className={classes.section}>
      <GridContainer justify="center">
        <GridItem cs={12} sm={12} md={8}>
          <h2 className={classes.title}>Request a course</h2>
          <h4 className={classes.description}>
            We would love to hear from you. Simply complete the form below or email our team at academy.sk@techdata.com
          </h4>
          <form>
            <GridContainer>
              <GridItem xs={12} sm={12} md={6}>
                <CustomInput
                  labelText={userName}
                  id="name"
                  formControlProps={{
                    fullWidth: true
                  }}
                />
              </GridItem>
              <GridItem xs={12} sm={12} md={6}>
                <CustomInput
                  labelText={userEmail}
                  id="email"
                  formControlProps={{
                    fullWidth: true
                  }}
                />
              </GridItem>
              <CustomInput
                labelText="Your Message"
                id="message"
                formControlProps={{
                  fullWidth: true,
                  className: classes.textArea
                }}
                inputProps={{
                  multiline: true,
                  rows: 5
                }}
              />
              <NotificationContainer />

              <GridContainer justify="center">      
                <GridItem xs={12} sm={12} md={4} className={classes.textCenter}>
                  <Button color="primary" onClick ={() => handelSubmit()}>Send</Button>
                </GridItem>
              </GridContainer>
              <br />
            </GridContainer>
          </form>
        </GridItem>
      </GridContainer>
    </div>
  );
}
