import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons

// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";

import styles from "assets/jss/material-kit-react/views/landingPageSections/workStyle.js";
import SnackbarContent from "components/Snackbar/SnackbarContent.js";
import Warning from "@material-ui/icons/Warning";

const useStyles = makeStyles(styles);
const axios = require('axios');
const internalApi ="http://localhost:9000/"

export default function WorkSection() {
  const classes = useStyles();
  let username = localStorage.getItem("username") && localStorage.getItem("username") != "null" ? localStorage.getItem("username") :"Your Name"
  let useremail = localStorage.getItem("email") && localStorage.getItem("email") != "null" || "Your Email"
  function handelSubmit(){
    if(!(document.getElementById("name").value &&  document.getElementById("email").value) &&  document.getElementById("message").value){
      return  (<SnackbarContent
          message={
            <span>
              <b>WARNING ALERT:</b> Please ,fill all the required fields...
            </span>
          }
          close
          color="warning"
          icon={Warning}
        />)
    }
    axios.post(`${internalApi}api/requestCourse`, {
      "email": document.getElementById("email").value,
      "name": document.getElementById("name").value,
      "message": document.getElementById("message").value
    })
    .then(function (response) {
      console.log(response,"res/////////////////")
      if(response.data.status === 200){
        localStorage.setItem("token", response.data.token);
        localStorage.setItem("username", response.data.username);
        localStorage.setItem("email", response.data.email);
        window.location.replace(window.location.origin)
      }else{
        console.log("notify")
        return  (<SnackbarContent
        message={
          <span>
            <b>WARNING ALERT:</b> You{"'"}ve got some friends nearby, stop
            looking at your phone and find them...
          </span>
        }
        close
        color="warning"
        icon={Warning}
      />)
      }
    })
    .catch(function (error) {
      return error;
    });
  }
  return (
    <div className={classes.section}>
      <GridContainer justify="center">
        <GridItem cs={12} sm={12} md={8}>
          <h2 className={classes.title}>Request a course</h2>
          <h4 className={classes.description}>
            Divide details about your product or agency work into parts. Write a
            few lines about each one and contact us about any further
            collaboration. We will responde get back to you in a couple of
            hours.
          </h4>
          <form>
            <GridContainer>
              <GridItem xs={12} sm={12} md={6}>
                <CustomInput
                  labelText={username}
                  id="name"
                  formControlProps={{
                    fullWidth: true
                  }}
                />
              </GridItem>
              <GridItem xs={12} sm={12} md={6}>
                <CustomInput
                  labelText={useremail}
                  id="email"
                  formControlProps={{
                    fullWidth: true
                  }}
                />
              </GridItem>
              <CustomInput
                labelText="Your Message"
                id="message"
                formControlProps={{
                  fullWidth: true,
                  className: classes.textArea
                }}
                inputProps={{
                  multiline: true,
                  rows: 5
                }}
              />
              <GridContainer justify="center">
                <GridItem xs={12} sm={12} md={4} className={classes.textCenter}>
                  <Button color="primary" onClick ={() => handelSubmit()}>Send</Button>
                </GridItem>
              </GridContainer>
            </GridContainer>
          </form>
        </GridItem>
      </GridContainer>
    </div>
  );
}
