import * as React from '@material-ui/core/Link/node_modules/react';
import { OverridableComponent, SimplifiedPropsOf } from '@material-ui/core/OverridableComponent';
import { Omit } from '@material-ui/core/Link/node_modules/@material-ui/types';
import { TypographyProps } from '@material-ui/core/Typography';

declare const Link: OverridableComponent<{
  props: LinkBaseProps & {
    TypographyClasses?: TypographyProps['classes'];
    underline?: 'none' | 'hover' | 'always';
  };
  defaultComponent: 'a';
  classKey: LinkClassKey;
}>;

export type LinkClassKey =
  | 'root'
  | 'underlineNone'
  | 'underlineHover'
  | 'underlineAlways'
  | 'button'
  | 'focusVisible';

export type LinkBaseProps = React.AnchorHTMLAttributes<HTMLAnchorElement> &
  Omit<TypographyProps, 'component'>;

export type LinkProps = SimplifiedPropsOf<typeof Link>;

export default Link;
