import * as React from '@material-ui/core/TablePagination/node_modules/react';
import { OverridableComponent, SimplifiedPropsOf } from '@material-ui/core/OverridableComponent';
import { Omit } from '@material-ui/core/TablePagination/node_modules/@material-ui/types';
import { TablePaginationActionsProps } from '@material-ui/core/TablePagination/TablePaginationActions';
import { TableCellProps } from '@material-ui/core/TableCell';
import { IconButtonProps } from '@material-ui/core/IconButton';
import { SelectProps } from '@material-ui/core/Select';

export interface LabelDisplayedRowsArgs {
  from: number;
  to: number;
  count: number;
  page: number;
}

declare const TablePagination: OverridableComponent<{
  props: TablePaginationBaseProps & {
    ActionsComponent?: React.ElementType<TablePaginationActionsProps>;
    backIconButtonProps?: Partial<IconButtonProps>;
    count: number;
    labelDisplayedRows?: (paginationInfo: LabelDisplayedRowsArgs) => React.ReactNode;
    labelRowsPerPage?: React.ReactNode;
    nextIconButtonProps?: Partial<IconButtonProps>;
    onChangePage: (event: React.MouseEvent<HTMLButtonElement> | null, page: number) => void;
    onChangeRowsPerPage?: React.ChangeEventHandler<HTMLTextAreaElement | HTMLInputElement>;
    page: number;
    rowsPerPage: number;
    rowsPerPageOptions?: number[];
    SelectProps?: Partial<SelectProps>;
  };
  defaultComponent: React.ComponentType<TablePaginationBaseProps>;
  classKey: TablePaginationClassKey;
}>;

export type TablePaginationClassKey =
  | 'root'
  | 'toolbar'
  | 'spacer'
  | 'menuItem'
  | 'caption'
  | 'input'
  | 'selectRoot'
  | 'select'
  | 'selectIcon'
  | 'actions';

export type TablePaginationBaseProps = Omit<TableCellProps, 'classes' | 'component'>;

export type TablePaginationProps = SimplifiedPropsOf<typeof TablePagination>;

export default TablePagination;
